mapper.select:

select

from tbl_orders
where USERID = #{USERID,jdbcType=DECIMAL} and  projartid=#{articleid,jdbcType=DECIMAL}


select * from (select tt.*, ROWNUM as rowno from (select  from tbl_orders t
where SITEID = #{SITEID,jdbcType=DECIMAL} and USERID = #{UID,jdbcType=INTEGER}
order by CREATEDATE desc) tt where ROWNUM <= #{ENDROW,jdbcType=DECIMAL}) table_alias where table_alias.rowno >= #{BEGINROW,jdbcType=DECIMAL}

mapper.update:

update tbl_orders
set lastupdate = #{lastupdate,jdbcType=TIMESTAMP},
STATUS = #{STATUS,jdbcType=SMALLINT}
where ORDERID = #{ORDERID,jdbcType=BIGINT}
