mapper.update:

update TBL_MEMBERS
set COMPANY = #{COMPANY,jdbcType=VARCHAR},
LATITUDE = #{LATITUDE,jdbcType=DOUBLE},
LONGITUDE = #{LONGITUDE,jdbcType=DOUBLE}
where USERID = #{USERID,jdbcType=VARCHAR}


update TBL_MEMBERS
set DFLAG = #{DFLAG,jdbcType=DECIMAL}
where USERID = #{USERID,jdbcType=VARCHAR}

mapper.select:
select * from (select a.*, ROWNUM RN from( select
from TBL_MEMBERS t where 1=1
and t.shequ like CONCAT(CONCAT('',#{area,jdbcType=VARCHAR}), '%')
and t.company like CONCAT(CONCAT('%',#{keyword,jdbcType=VARCHAR}), '%')
) a)
where RN between #{startRec} and #{endRec}

mapper.update:

